var MyFirstSI = Class.create();

MyFirstSI.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    type: 'MyFirstSI',

	MyFirstSI:function(){
		var sysid = this.getParameter('sysparm_user_name');
		gs.addInfoMessage('sys id: ' + sysid);
		var gr = new GlideRecord('sys_user');
		gr.addQuery('sys_id', sysid);
		gr.query();
		var phone = '';
		if(gr.next()) {
			gs.addInfoMessage("it's here " + gr.mobile_phone);
			// phone = gr.get('mobile_phone');
			phone = gr.mobile_phone;
			if(phone != '')
				return phone;
			return 'Does not have a phone number';
		}
		else {
			return 'User not found!';
		}
// 		if(gr.get('mobile_phone')){
// 			phone = gr.get('mobile_phone');
// 			return phone.getDisplayValue;
// 		} else {
// 			return 'Does not have a phone number';
// 		}

	},

	_privateFunction: function(){

	}
});
